package com.aop.schema;

import java.text.ParseException;
import java.util.Random;
import org.apache.commons.codec.binary.Base64;

public class Test {
	
	public static void main( String[] args ) throws ParseException{

System.out.println(randomNumberGenerator(1001,9999));	 

	byte[] dec = Base64.decodeBase64("".getBytes());
	
	System.out.println(new String(dec));
	}
	
	public static String randomNumberGenerator(int startingValue,int endValue){
        Random random = new Random();
        Integer randomNumber = random.nextInt(endValue - startingValue) + startingValue;

        return randomNumber.toString();
    }
}
